INSERT INTO `dangxiaotiku_fzdx` VALUES (1170, '在党组织讨论决定对党员的党纪处分或作出鉴定时，本人有权参加和进行申辩，其他党员可以为他作证和辩护。', '对', '错', '', '', '', 'A', 5);
INSERT INTO `dangxiaotiku_fzdx` VALUES (1171, '对党的决议和政策如有不同意见，在不予以执行的情况下，还可以声明保留，并且可以把自己的意见向党的上级组织直至中央提出。', '对', '错', '', '', NULL, 'B', 4);
