INSERT INTO `dangxiaotiku_fzdx` VALUES (1180, '中国共产党的三大作风是理论联系实际，密切联系群众，批评与自我批评。', '对', '错', '', '', NULL, 'A', 7);
INSERT INTO `dangxiaotiku_fzdx` VALUES (1181, '修改党的章程是党的全国代表大会的职权。', '对', '错', '', '', '', 'A', 4);
