INSERT INTO `dangxiaotiku_fzdx` VALUES (1184, '在中央和地方国家机关、人民团体、经济组织、文化组织和其他非党组织的领导机关中，都应成立党组。', '对', '错', '', '', NULL, 'B', 7);
