INSERT INTO `dangxiaotiku_fzdx` VALUES (1187, '党在任何时候都要坚持权为民所用、情为民所系、利为民所谋。', '对', '错', '', '', '', 'A', 3);
INSERT INTO `dangxiaotiku_fzdx` VALUES (1188, '我党主张唯物主义，不提倡宗教信仰自由。', '对', '错', '', '', NULL, 'B', 8);
INSERT INTO `dangxiaotiku_fzdx` VALUES (1189, '党必须适应形势的发展和情况的变化，完善领导体制，改进领导方式，增强执政能力。', '对', '错', '', '', NULL, 'A', 5);
INSERT INTO `dangxiaotiku_fzdx` VALUES (1190, '民族精神的核心是爱国主义。', '对', '错', '', '', '', 'A', 6);
